import React, { useEffect, useState } from "react";
import axios from "axios";

const PetitionList = () => {
  const [petitions, setPetitions] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/get_petitions")
      .then((response) => {
        setPetitions(response.data);
      })
      .catch((error) => {
        console.error("Error fetching petitions:", error);
      });
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Petitions List</h2>
      <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
        <thead className="bg-blue-600 text-white">
          <tr>
            <th className="p-3 text-left">ID</th>
            <th className="p-3 text-left">Username</th>
            <th className="p-3 text-left">Details</th>
            <th className="p-3 text-left">Document</th>
            <th className="p-3 text-left">Status</th>
            <th className="p-3 text-left">Importance</th>
            <th className="p-3 text-left">Created At</th>
          </tr>
        </thead>
        <tbody>
          {petitions.map((petition) => (
            <tr key={petition.id} className="border-b">
              <td className="p-3">{petition.id}</td>
              <td className="p-3">{petition.username}</td>
              <td className="p-3">{petition.details}</td>
              <td className="p-3">{petition.document || "No document uploaded"}</td>
              <td className="p-3">{petition.status}</td>
              <td className="p-3">{petition.importance}</td>
              <td className="p-3">{new Date(petition.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PetitionList;
