import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navigation Bar */}
      <nav className="bg-blue-600 p-4 text-white shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Grievance System</h1>
          <div className="space-x-6">
            <Link to="/" className="hover:underline">Home</Link>
            <Link to="/register" className="hover:underline">Register Complaint</Link>
            <Link to="/status" className="hover:underline">Check Status</Link>
            <Link to="/upload" className="hover:underline">Upload Documents</Link>
          </div>
        </div>
      </nav>

      {/* Home Content */}
      <div className="flex flex-col items-center justify-center h-[80vh] px-6 text-center">
        <h1 className="text-5xl font-bold text-blue-700 mb-4">Welcome to the Grievance Handling System</h1>
        <p className="text-lg text-gray-700 mb-6 max-w-xl">
          Submit your complaints, track their status, and receive timely updates.
        </p>
        <div className="space-x-4">
          <Link to="/register" className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg shadow-md transition-all duration-300">
            Register a Complaint
          </Link>
          <Link to="/status" className="bg-green-500 hover:bg-green-600 text-white px-6 py-3 rounded-lg shadow-md transition-all duration-300">
            Check Status
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
