import React, { useState } from "react";
import axios from "axios";

const PetitionForm = () => {
  const [formData, setFormData] = useState({
    username: "",
    details: "",
    department: "Electricity"
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://127.0.0.1:5000/submit_petition", formData);
      setMessage(res.data.message + ` (Importance: ${res.data.importance})`);
    } catch (error) {
      setMessage("Error submitting petition.");
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow-md">
      <h2 className="text-xl font-bold mb-4">Submit a Petition</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Your Name" value={formData.username} onChange={handleChange} className="border p-2 w-full mb-2" required />
        <textarea name="details" placeholder="Petition Details" value={formData.details} onChange={handleChange} className="border p-2 w-full mb-2" required></textarea>
        <select name="department" value={formData.department} onChange={handleChange} className="border p-2 w-full mb-2">
          <option value="Electricity">Electricity</option>
          <option value="Transport">Transport</option>
          <option value="Education">Education</option>
        </select>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Submit</button>
      </form>
      {message && <p className="mt-2">{message}</p>}
    </div>
  );
};

export default PetitionForm;
