import React, { useState } from "react";
import axios from "axios";

const ComplaintRegistration = () => {
  const [username, setUsername] = useState("");
  const [details, setDetails] = useState("");
  const [department, setDepartment] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:5000/submit_petition", {
        username,
        details,
        department
      });
      alert(response.data.message + " | Importance: " + response.data.importance);
    } catch (error) {
      console.error("Error submitting petition:", error);
      alert("Error submitting petition");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Register a Complaint</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Your Name" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          className="border p-2 w-full mb-2"
        />
        <textarea 
          placeholder="Complaint Details" 
          value={details} 
          onChange={(e) => setDetails(e.target.value)} 
          className="border p-2 w-full mb-2"
        ></textarea>
        <select 
          value={department} 
          onChange={(e) => setDepartment(e.target.value)} 
          className="border p-2 w-full mb-2"
        >
          <option value="">Select Department</option>
          <option value="Electricity">Electricity</option>
          <option value="Transport">Transport</option>
          <option value="Education">Education</option>
        </select>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2">Submit</button>
      </form>
    </div>
  );
};

export default ComplaintRegistration;
