import React, { useEffect, useState } from "react";
import axios from "axios";

const PetitionList = () => {
  const [petitions, setPetitions] = useState([]);

  useEffect(() => {
    fetchPetitions();
    const interval = setInterval(fetchPetitions, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchPetitions = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/get_petitions");
      setPetitions(response.data);
    } catch (error) {
      console.error("Error fetching petitions:", error);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-4">All Petitions</h1>
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border px-4 py-2">ID</th>
            <th className="border px-4 py-2">Username</th>
            <th className="border px-4 py-2">Details</th>
            <th className="border px-4 py-2">Department</th>
            <th className="border px-4 py-2">Status</th>
            <th className="border px-4 py-2">Importance</th>
          </tr>
        </thead>
        <tbody>
          {petitions.map((petition) => (
            <tr key={petition.id} className="hover:bg-gray-100">
              <td className="border px-4 py-2">{petition.id}</td>
              <td className="border px-4 py-2">{petition.username}</td>
              <td className="border px-4 py-2">{petition.details}</td>
              <td className="border px-4 py-2">{petition.department}</td>
              <td className="border px-4 py-2">{petition.status}</td>
              <td className="border px-4 py-2">{petition.importance}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PetitionList;
