import React, { useEffect, useState } from "react";
import axios from "axios";

const StatusChecking = () => {
  const [petitions, setPetitions] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/get_petitions")
      .then((response) => setPetitions(response.data))
      .catch((error) => console.error("Error fetching petitions:", error));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Petition Status</h2>
      <table className="table-auto w-full border-collapse border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">ID</th>
            <th className="border p-2">User</th>
            <th className="border p-2">Department</th>
            <th className="border p-2">Details</th>
            <th className="border p-2">Status</th>
            <th className="border p-2">Importance</th>
          </tr>
        </thead>
        <tbody>
          {petitions.map((petition) => (
            <tr key={petition.id} className="border">
              <td className="border p-2">{petition.id}</td>
              <td className="border p-2">{petition.username}</td>
              <td className="border p-2">{petition.department}</td>
              <td className="border p-2">{petition.details}</td>
              <td className="border p-2">{petition.status}</td>
              <td className={`border p-2 ${petition.importance === "High" ? "text-red-500" : petition.importance === "Medium" ? "text-orange-500" : "text-green-500"}`}>
                {petition.importance}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StatusChecking;
