import React, { useState } from "react";
import axios from "axios";

const DocumentUpload = () => {
  const [file, setFile] = useState(null);
  const [petitionId, setPetitionId] = useState("");
  const [message, setMessage] = useState({ text: "", type: "" });

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage({ text: "", type: "" });

    if (!file || !petitionId) {
      setMessage({ text: "‚ö†Ô∏è Please provide a valid Petition ID and select a file.", type: "error" });
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("petition_id", petitionId);

    try {
      const res = await axios.post("http://127.0.0.1:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setMessage({ text: `‚úÖ ${res.data.message}`, type: "success" });
      setFile(null);
      setPetitionId("");
    } catch (error) {
      console.error("Upload error:", error);
      setMessage({
        text: error.response?.data?.error || "‚ùå Error uploading document. Try again.",
        type: "error",
      });
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold mb-4">üìÑ Upload Supporting Documents</h2>
      {message.text && (
        <p className={`mb-4 ${message.type === "success" ? "text-green-600" : "text-red-600"}`}>
          {message.text}
        </p>
      )}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 font-semibold">Petition ID:</label>
          <input
            type="text"
            placeholder="Enter Petition ID"
            value={petitionId}
            onChange={(e) => setPetitionId(e.target.value)}
            required
            className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-semibold">Upload File:</label>
          <input
            type="file"
            onChange={handleFileChange}
            required
            className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition">
          üì§ Upload Document
        </button>
      </form>
    </div>
  );
};

export default DocumentUpload;
