import React, { useState } from "react";
import axios from "axios";

const StatusChecking = () => {
  const [petitionId, setPetitionId] = useState("");
  const [status, setStatus] = useState(null);
  const [error, setError] = useState(null);

  const checkStatus = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:5000/status/${petitionId}`);
      setStatus(res.data);
      setError(null);
    } catch (err) {
      setError("Petition not found or invalid ID.");
      setStatus(null);
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow-md">
      <h2 className="text-xl font-bold mb-4">Check Petition Status</h2>
      <input
        type="text"
        placeholder="Enter Petition ID"
        value={petitionId}
        onChange={(e) => setPetitionId(e.target.value)}
        className="border p-2 w-full mb-2"
      />
      <button onClick={checkStatus} className="bg-blue-500 text-white px-4 py-2 rounded">Check Status</button>

      {status && (
        <div className="mt-4 p-2 bg-gray-100 rounded">
          <p><strong>ID:</strong> {status.id}</p>
          <p><strong>Status:</strong> {status.status}</p>
          <p><strong>Importance:</strong> {status.importance}</p>
        </div>
      )}
      {error && <p className="text-red-500 mt-2">{error}</p>}
    </div>
  );
};

export default StatusChecking;
