import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="p-6 text-center">
      <h1 className="text-4xl font-bold mb-4">Welcome to the Grievance Handling System</h1>
      <p className="text-lg text-gray-600 mb-6">Submit your complaints and track their status.</p>
      <div className="space-x-4">
        <Link to="/register" className="bg-blue-500 text-white px-4 py-2 rounded">Register a Complaint</Link>
        <Link to="/status" className="bg-green-500 text-white px-4 py-2 rounded">Check Status</Link>
      </div>
    </div>
  );
};

export default Home;
