import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { Home, ComplaintRegistration, StatusChecking, DocumentUpload } from "";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {/* Navigation Bar */}
        <nav className="bg-blue-600 p-4 text-white shadow-lg">
          <div className="container mx-auto flex justify-between items-center">
            {/* Brand Name */}
            <h1 className="text-2xl font-bold">Grievance Dashboard</h1>

            {/* Mobile Menu Button */}
            <button
              className="md:hidden block text-white focus:outline-none"
              onClick={() => setIsOpen(!isOpen)}
            >
              {isOpen ? "✖" : "☰"}
            </button>

            {/* Nav Links (Desktop & Mobile) */}
            <div className={`md:flex space-x-6 ${isOpen ? "block" : "hidden"} md:block`}>
              <Link to="/" className="hover:underline block py-2">Home</Link>
              <Link to="/register" className="hover:underline block py-2">Complaint Registration</Link>
              <Link to="/status" className="hover:underline block py-2">Status Checking</Link>
              <Link to="/upload" className="hover:underline block py-2">Document Upload</Link>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <div className="container mx-auto p-6">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/register" element={<ComplaintRegistration />} />
            <Route path="/status" element={<StatusChecking />} />
            <Route path="/upload" element={<DocumentUpload />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
