import React, { useState } from "react";
import axios from "axios";

const DocumentUpload = () => {
  const [file, setFile] = useState(null);
  const [petitionId, setPetitionId] = useState("");
  const [message, setMessage] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !petitionId) {
      setMessage("Please provide a valid petition ID and select a file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("petition_id", petitionId);

    try {
      const res = await axios.post("http://127.0.0.1:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setMessage(res.data.message);
    } catch (error) {
      setMessage("Error uploading document.");
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow-md">
      <h2 className="text-xl font-bold mb-4">Upload Supporting Documents</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Enter Petition ID" value={petitionId} onChange={(e) => setPetitionId(e.target.value)} className="border p-2 w-full mb-2" required />
        <input type="file" onChange={handleFileChange} className="border p-2 w-full mb-2" required />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Upload</button>
      </form>
      {message && <p className="mt-2">{message}</p>}
    </div>
  );
};

export default DocumentUpload;
